internal class Cache : ICache {

    private var serverList: TargetProviderServerList? = null

    override fun setServerList(serverList: TargetProviderServerList): Result<Unit> {
        this.serverList = serverList
        return Result.success(Unit)
    }

    override fun getServerList(): Result<TargetProviderServerList> =
        runCatching {
            serverList
                ?: throw TargetProviderError(code = TargetProviderErrorCode.UNKNOWN_SERVER_LIST)
        }
}
